 import React from 'react';
import { FiBookOpen } from 'react-icons/fi';

const BookCard = ({ book }) => {
  return (
    <div
      className={`bg-white rounded-xl shadow-md overflow-hidden relative border ${
        book.isIssued ? 'border-red-500' : 'border-transparent'
      }`}
    >
      {/* Issued Badge */}
      {book.isIssued && (
        <span className="absolute top-2 right-2 bg-red-500 text-white text-xs font-semibold px-2 py-1 rounded-full shadow">
          Issued
        </span>
      )}

      {/* Book Cover */}
      <div className="h-48 bg-gray-100 flex items-center justify-center overflow-hidden">
        {book.coverImage ? (
          <img
            src={book.coverImage}
            alt={book.title}
            className="h-full w-auto object-cover"
          />
        ) : (
          <FiBookOpen className="text-gray-400 text-5xl" />
        )}
      </div>

      {/* Book Info */}
      <div className="p-4">
        <span className="text-xs font-semibold text-indigo-600 uppercase">
          {book.category}
        </span>
        <h3 className="mt-1 text-lg font-bold text-gray-800">{book.title}</h3>
        <p className="text-gray-600 text-sm">by {book.author}</p>

        {/* Rating Stars */}
        <div className="flex items-center mt-2">
          {Array.from({ length: 5 }).map((_, index) => (
            <span key={index}>
              {index < Math.round(book.avgRating || 0) ? '⭐' : '☆'}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default BookCard;
