 import axios from 'axios';

const API_URL = '/api/books';

// Get all books
const getBooks = async (params) => {
  const response = await axios.get(API_URL, { params });
  return response.data;
};

// Issue a book
const issueBook = async (bookId, token) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`
    }
  };
  
  const response = await axios.put(`${API_URL}/${bookId}/issue`, {}, config);
  return response.data;
};

// Return a book
const returnBook = async (bookId, token) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`
    }
  };
  
  const response = await axios.put(`${API_URL}/${bookId}/return`, {}, config);
  return response.data;
};

// Rate a book
const rateBook = async (bookId, rating, token) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`
    }
  };
  
  const response = await axios.post(`${API_URL}/${bookId}/rate`, { rating }, config);
  return response.data;
};

// Admin: Add a new book
const addBook = async (bookData, token) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
      'Content-Type': 'multipart/form-data'
    }
  };
  
  const response = await axios.post(`${API_URL}/admin/books`, bookData, config);
  return response.data;
};

export default {
  getBooks,
  issueBook,
  returnBook,
  rateBook,
  addBook
};
