 // frontend/src/pages/AdminDashboard.jsx
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FiBook, FiUsers, FiClock, FiPlusCircle } from "react-icons/fi";
import axios from "axios";

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalBooks: 0,
    totalUsers: 0,
    borrowedBooks: 0,
    recentActivity: [],
  });
  const [loading, setLoading] = useState(true);

  const fetchStats = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("/api/admin/stats", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setStats(res.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStats(); // Initial load
    const interval = setInterval(fetchStats, 10000); // Refresh every 10s
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-8">Admin Dashboard</h1>

      {loading ? (
        <div className="flex justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-600"></div>
        </div>
      ) : (
        <>
          {/* Stats Section */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-white rounded-xl shadow-md p-6 flex items-center hover:shadow-lg transition">
              <div className="bg-indigo-100 p-4 rounded-full mr-4">
                <FiBook className="text-indigo-600 text-2xl" />
              </div>
              <div>
                <h2 className="text-2xl font-bold text-gray-800">
                  {stats.totalBooks}
                </h2>
                <p className="text-gray-600">Total Books</p>
              </div>
            </div>

            <div className="bg-white rounded-xl shadow-md p-6 flex items-center hover:shadow-lg transition">
              <div className="bg-green-100 p-4 rounded-full mr-4">
                <FiUsers className="text-green-600 text-2xl" />
              </div>
              <div>
                <h2 className="text-2xl font-bold text-gray-800">
                  {stats.totalUsers}
                </h2>
                <p className="text-gray-600">Total Users</p>
              </div>
            </div>

            <div className="bg-white rounded-xl shadow-md p-6 flex items-center hover:shadow-lg transition">
              <div className="bg-yellow-100 p-4 rounded-full mr-4">
                <FiClock className="text-yellow-600 text-2xl" />
              </div>
              <div>
                <h2 className="text-2xl font-bold text-gray-800">
                  {stats.borrowedBooks}
                </h2>
                <p className="text-gray-600">Borrowed Books</p>
              </div>
            </div>

            <Link
              to="/admin/add-book"
              className="bg-white rounded-xl shadow-md p-6 flex items-center hover:shadow-lg transition"
            >
              <div className="bg-blue-100 p-4 rounded-full mr-4">
                <FiPlusCircle className="text-blue-600 text-2xl" />
              </div>
              <div>
                <h2 className="text-2xl font-bold text-gray-800">Add Book</h2>
                <p className="text-gray-600">New Book</p>
              </div>
            </Link>
          </div>

          {/* Recent Activity */}
          <div className="bg-white rounded-xl shadow-md p-6">
            <h2 className="text-xl font-bold text-gray-800 mb-4">
              Recent Activity
            </h2>
            <div className="space-y-4">
              {stats.recentActivity && stats.recentActivity.length > 0 ? (
                stats.recentActivity.map((activity, index) => (
                  <div key={index} className="p-4 bg-gray-50 rounded-lg">
                    <p className="text-gray-600">{activity.message}</p>
                    <p className="text-gray-400 text-sm">
                      {new Date(activity.timestamp).toLocaleString()}
                    </p>
                  </div>
                ))
              ) : (
                <p className="text-gray-500">No recent activity</p>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default AdminDashboard;
