 import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import BookCard from './BookCard';
import SearchBar from './SearchBar';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  const categories = [
    'All',
    'F1 Racing',
    'Motorsports',
    'Vintage Cars',
    'Motorbikes',
    'Automotive Engineering'
  ];

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/books', {
          params: {
            search: searchTerm,
            category: selectedCategory === 'All' ? null : selectedCategory
          },
          headers: token ? { Authorization: `Bearer ${token}` } : {}
        });

        let booksData = [];
        if (Array.isArray(res.data)) {
          booksData = res.data;
        } else if (Array.isArray(res.data.books)) {
          booksData = res.data.books;
        }

        // ✅ Normalize rating value to avoid crashes
        booksData = booksData.map(book => ({
          ...book,
          avgRating: Number(book?.avgRating) || 0
        }));

        setBooks(booksData);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setBooks([]);
        setLoading(false);
      }
    };

    fetchBooks();
  }, [searchTerm, selectedCategory]);

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">
        Automobile & Racing Books
      </h1>

      <SearchBar
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        categories={categories}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
      />

      {loading ? (
        <div className="flex justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-600"></div>
        </div>
      ) : books.length === 0 ? (
        <div className="text-center py-12">
          <p className="text-xl text-gray-600">No books found</p>
          <p className="text-gray-500 mt-2">Try adjusting your search or filter</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {books.map(book => (
            <Link to={`/books/${book._id}`} key={book._id}>
              <BookCard book={book} />
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default BookList;
