 import React, { useState, useContext } from "react";
import { AuthContext } from "../../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { motion, AnimatePresence } from "framer-motion";
import {
  FiUserPlus,
  FiLogIn,
  FiMail,
  FiLock,
  FiUser,
} from "react-icons/fi";

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const { register, login } = useContext(AuthContext);
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const { name, email, password } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        await login(email, password);
        toast.success("✅ Logged in successfully");
      } else {
        await register(name, email, password);
        toast.success("✅ Registered successfully");
      }
      navigate("/");
    } catch (err) {
      toast.error("❌ " + (isLogin ? "Invalid credentials" : "Registration failed"));
    }
  };

  const inputContainer = {
    position: "relative",
    marginBottom: "15px",
  };

  const iconStyle = {
    position: "absolute",
    top: "50%",
    left: "12px",
    transform: "translateY(-50%)",
    color: "#888",
  };

  const inputStyle = {
    width: "100%",
    padding: "12px 12px 12px 38px",
    border: "1px solid #ddd",
    borderRadius: "8px",
    fontSize: "14px",
    outline: "none",
    transition: "border 0.2s, box-shadow 0.2s",
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "linear-gradient(135deg, #2575fc, #6a11cb)",
        padding: "20px",
      }}
    >
      <motion.div
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        style={{
          background: "#fff",
          padding: "30px",
          width: "100%",
          maxWidth: "420px",
          borderRadius: "15px",
          boxShadow: "0 8px 20px rgba(0,0,0,0.15)",
          textAlign: "center",
        }}
      >
        <AnimatePresence mode="wait">
          <motion.div
            key={isLogin ? "login" : "register"}
            initial={{ opacity: 0, x: isLogin ? 50 : -50 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: isLogin ? -50 : 50 }}
            transition={{ duration: 0.4 }}
          >
            <h2 style={{ marginBottom: "5px", fontSize: "24px", color: "#333" }}>
              {isLogin ? (
                <FiLogIn style={{ marginRight: "5px" }} />
              ) : (
                <FiUserPlus style={{ marginRight: "5px" }} />
              )}
              {isLogin ? "Sign In" : "Register"}
            </h2>
            <p style={{ fontSize: "14px", color: "#777", marginBottom: "20px" }}>
              {isLogin
                ? "Access your Racing Books account"
                : "Create a new Racing Books account"}
            </p>

            <form onSubmit={onSubmit}>
              {!isLogin && (
                <div style={inputContainer}>
                  <FiUser style={iconStyle} />
                  <input
                    type="text"
                    name="name"
                    placeholder="Full Name"
                    value={name}
                    onChange={onChange}
                    required
                    style={inputStyle}
                  />
                </div>
              )}
              <div style={inputContainer}>
                <FiMail style={iconStyle} />
                <input
                  type="email"
                  name="email"
                  placeholder="Email Address"
                  value={email}
                  onChange={onChange}
                  required
                  style={inputStyle}
                />
              </div>
              <div style={inputContainer}>
                <FiLock style={iconStyle} />
                <input
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={password}
                  onChange={onChange}
                  required
                  minLength="6"
                  style={inputStyle}
                />
              </div>

              <button
                type="submit"
                style={{
                  width: "100%",
                  padding: "12px",
                  background: "linear-gradient(90deg, #2575fc, #6a11cb)",
                  color: "#fff",
                  border: "none",
                  borderRadius: "8px",
                  fontSize: "16px",
                  cursor: "pointer",
                  fontWeight: "bold",
                  transition: "opacity 0.2s",
                }}
              >
                {isLogin ? "Sign In" : "Register"}
              </button>
            </form>
          </motion.div>
        </AnimatePresence>

        <div style={{ marginTop: "15px", fontSize: "14px", color: "#555" }}>
          {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
          <span
            onClick={() => setIsLogin(!isLogin)}
            style={{
              color: "#2575fc",
              fontWeight: "bold",
              cursor: "pointer",
            }}
          >
            {isLogin ? "Register here" : "Login here"}
          </span>
        </div>
      </motion.div>
    </div>
  );
};

export default AuthPage;
