 import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";

const COLORS = ["#00C49F", "#FF8042"];

export default function AdminDashboard() {
  const [stats, setStats] = useState({
    totalBooks: 0,
    totalUsers: 0,
    borrowedBooks: 0,
    issuedCount: 0,
    notIssuedCount: 0,
    recentActivity: []
  });

  useEffect(() => {
    axios.get("/api/admin/stats", {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
    })
    .then(res => setStats(res.data))
    .catch(err => console.error(err));
  }, []);

  const chartData = [
    { name: "Users with Issued Books", value: stats.issuedCount },
    { name: "Users without Issued Books", value: stats.notIssuedCount }
  ];

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Admin Dashboard</h1>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <div className="bg-white shadow p-4 rounded">
          <h2 className="text-lg">Total Books</h2>
          <p className="text-3xl font-bold">{stats.totalBooks}</p>
        </div>
        <div className="bg-white shadow p-4 rounded">
          <h2 className="text-lg">Total Users</h2>
          <p className="text-3xl font-bold">{stats.totalUsers}</p>
        </div>
        <div className="bg-white shadow p-4 rounded">
          <h2 className="text-lg">Borrowed Books</h2>
          <p className="text-3xl font-bold">{stats.borrowedBooks}</p>
        </div>
        <div className="bg-white shadow p-4 rounded">
          <h2 className="text-lg">Add Book</h2>
          <Link to="/admin/add-book" className="text-blue-600 underline">
            + Add
          </Link>
        </div>
      </div>

      {/* Quick Actions + Pie Chart */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        <div className="bg-white shadow p-4 rounded">
          <h2 className="text-lg font-bold mb-3">Quick Actions</h2>
          <ul className="space-y-2">
            <li><Link to="/admin/manage-users" className="text-blue-600">ðŸ‘¤ Manage Users</Link></li>
            <li><Link to="/admin/borrowing-history" className="text-blue-600">ðŸ“œ View Borrowing History</Link></li>
            <li><Link to="/admin/add-book" className="text-blue-600">âž• Add New Book</Link></li>
          </ul>
        </div>

        <div className="bg-white shadow p-4 rounded">
          <h2 className="text-lg font-bold mb-3">Issued Books Chart</h2>
          <PieChart width={300} height={250}>
            <Pie
              data={chartData}
              cx="50%"
              cy="50%"
              labelLine={false}
              label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
              outerRadius={80}
              fill="#8884d8"
              dataKey="value"
            >
              {chartData.map((_, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip />
            <Legend />
          </PieChart>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="bg-white shadow p-4 rounded">
        <h2 className="text-lg font-bold mb-3">Recent Activity</h2>
        {stats.recentActivity.length > 0 ? (
          <ul>
            {stats.recentActivity.map((act, idx) => (
              <li key={idx} className="border-b py-1">{act.message}</li>
            ))}
          </ul>
        ) : (
          <p>No recent activity</p>
        )}
      </div>
    </div>
  );
}
