 import React, { useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { FiBook, FiUser, FiLogOut } from 'react-icons/fi';

const Navbar = () => {
  const { auth, logout } = useContext(AuthContext);
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <nav className="bg-indigo-700 text-white shadow-md">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex items-center">
              <FiBook className="h-8 w-8 mr-2" />
              <span className="font-bold text-xl">Racing Books</span>
            </Link>
          </div>
          
          <div className="hidden md:block">
            <div className="ml-10 flex items-baseline space-x-4">
              <Link 
                to="/" 
                className={`px-3 py-2 rounded-md text-sm font-medium ${
                  isActive('/') ? 'bg-indigo-900' : 'hover:bg-indigo-600'
                } transition`}
              >
                Books
              </Link>
              
              {auth.isAuthenticated && (
                <Link 
                  to="/profile" 
                  className={`px-3 py-2 rounded-md text-sm font-medium ${
                    isActive('/profile') ? 'bg-indigo-900' : 'hover:bg-indigo-600'
                  } transition`}
                >
                  My Profile
                </Link>
              )}
              
              {auth.isAuthenticated && auth.user?.role === 'admin' && (
                <Link 
                  to="/admin" 
                  className={`px-3 py-2 rounded-md text-sm font-medium ${
                    isActive('/admin') ? 'bg-indigo-900' : 'hover:bg-indigo-600'
                  } transition`}
                >
                  Admin Dashboard
                </Link>
              )}
            </div>
          </div>
          
          <div className="flex items-center">
            {auth.isAuthenticated ? (
              <div className="flex items-center">
                <div className="flex items-center mr-4">
                  {auth.user?.profilePhoto ? (
                    <img 
                      src={auth.user.profilePhoto} 
                      alt={auth.user.name} 
                      className="w-8 h-8 rounded-full mr-2"
                    />
                  ) : (
                    <div className="bg-gray-200 border-2 border-dashed rounded-full w-8 h-8 flex items-center justify-center text-gray-500">
                      <FiUser />
                    </div>
                  )}
                  <span className="hidden sm:inline">{auth.user?.name}</span>
                </div>
                <button
                  onClick={logout}
                  className="flex items-center text-sm font-medium text-white hover:text-gray-200 transition"
                >
                  <FiLogOut className="mr-1" /> Logout
                </button>
              </div>
            ) : (
              <div className="flex items-center space-x-4">
                <Link 
                  to="/login" 
                  className="px-3 py-2 rounded-md text-sm font-medium hover:bg-indigo-600 transition"
                >
                  Login
                </Link>
                <Link 
                  to="/register" 
                  className="px-3 py-2 bg-white text-indigo-700 rounded-md text-sm font-medium hover:bg-gray-100 transition"
                >
                  Register
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
