 import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { FiArrowLeft, FiUser, FiStar } from 'react-icons/fi';
import Rating from '../ui/Rating';

const BookDetails = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [rating, setRating] = useState(0);
  const [isIssued, setIsIssued] = useState(false);
  const [userRating, setUserRating] = useState(0);
  const [userIssuedBookId, setUserIssuedBookId] = useState(null);

  const fetchUserIssuedBook = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;
      const res = await axios.get('/api/users/me', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.data?.issuedBooks?.length > 0) {
        setUserIssuedBookId(res.data.issuedBooks[0]);
      } else {
        setUserIssuedBookId(null);
      }
    } catch (err) {
      console.error('Error fetching user issued book:', err);
    }
  };

  const fetchBook = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get(`/api/books/${id}`, {
        headers: token ? { Authorization: `Bearer ${token}` } : {}
      });
      setBook(res.data);

      if (
        token &&
        res.data.isIssued &&
        res.data.issuedTo?._id === JSON.parse(atob(token.split('.')[1])).user.id
      ) {
        setIsIssued(true);
      }

      if (token) {
        const ratingRes = await axios.get(`/api/books/${id}/rating`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        if (ratingRes.data) {
          setUserRating(ratingRes.data.rating);
        }
      }

      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBook();
    fetchUserIssuedBook();
    // eslint-disable-next-line
  }, [id]);

  const handleIssue = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(`/api/books/${id}/issue`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setIsIssued(true);
      await fetchBook();
      await fetchUserIssuedBook();
    } catch (err) {
      console.error(err);
    }
  };

  const handleReturn = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(`/api/books/${id}/return`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setIsIssued(false);
      await fetchBook();
      await fetchUserIssuedBook();
    } catch (err) {
      console.error(err);
    }
  };

  const handleRate = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(`/api/books/${id}/rate`, { rating }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      await fetchBook();
      setUserRating(rating);
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8 flex justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (!book) {
    return (
      <div className="container mx-auto px-4 py-8">
        <p className="text-center text-xl text-gray-600">Book not found</p>
        <Link to="/" className="mt-4 block text-center text-indigo-600 hover:underline">
          Back to books
        </Link>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <Link to="/" className="flex items-center text-indigo-600 hover:text-indigo-800 mb-6">
        <FiArrowLeft className="mr-1" /> Back to Books
      </Link>

      <div className="bg-white rounded-xl shadow-md overflow-hidden">
        <div className="md:flex">
          <div className="md:flex-shrink-0 p-8 flex items-center justify-center bg-gray-50">
            <img
              className="h-64 w-auto object-cover rounded-lg shadow-md"
              src={book.coverImage}
              alt={book.title}
            />
          </div>
          <div className="p-8">
            <div className="flex items-center justify-between">
              <span className="bg-indigo-100 text-indigo-800 text-xs font-semibold px-3 py-1 rounded-full">
                {book.category}
              </span>
              <Rating value={Number(book?.avgRating) || 0} />
            </div>

            <h1 className="text-3xl font-bold text-gray-800 mt-2">{book.title}</h1>
            <p className="text-xl text-gray-600 mt-1">by {book.author}</p>

            <div className="mt-6 space-y-4">
              {book.isIssued && book.issuedTo && (
                <div className="flex items-center">
                  <FiUser className="text-gray-500 mr-2" />
                  <span className="text-gray-700">Issued to: {book.issuedTo.name}</span>
                </div>
              )}
            </div>

            <div className="mt-6">
              <h3 className="text-lg font-medium text-gray-800">Description</h3>
              <p className="mt-2 text-gray-600">{book.description}</p>
            </div>

            <div className="mt-8 flex flex-wrap gap-4">
              {isIssued ? (
                <button
                  onClick={handleReturn}
                  className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-6 rounded-lg transition"
                >
                  Return Book
                </button>
              ) : (
                <button
                  onClick={handleIssue}
                  disabled={!!userIssuedBookId || book.isIssued}
                  className={`${!!userIssuedBookId || book.isIssued
                    ? 'bg-gray-400 cursor-not-allowed'
                    : 'bg-indigo-600 hover:bg-indigo-700'
                    } text-white font-bold py-3 px-6 rounded-lg transition`}
                >
                  {book.isIssued
                    ? 'Not Available'
                    : !!userIssuedBookId
                    ? 'You already have a book'
                    : 'Issue Book'}
                </button>
              )}

              {userRating > 0 ? (
                <div className="flex items-center bg-green-100 px-4 py-2 rounded-lg">
                  <FiStar className="text-yellow-500 mr-1" />
                  <span className="text-green-800">Your rating: {userRating}</span>
                </div>
              ) : (
                <div className="flex items-center">
                  <span className="mr-2 text-gray-700">Rate this book:</span>
                  <select
                    value={rating}
                    onChange={(e) => setRating(parseInt(e.target.value))}
                    className="border border-gray-300 rounded px-3 py-2"
                  >
                    <option value="0">Select</option>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                  </select>
                  <button
                    onClick={handleRate}
                    disabled={rating === 0}
                    className={`${rating === 0 ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-600 hover:bg-green-700'} text-white font-bold py-3 px-6 rounded-lg transition ml-2`}
                  >
                    Rate
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookDetails;
