 import React from 'react';
import Rating from '../ui/Rating';

const BookCard = ({ book }) => {
  if (!book) return null; // Prevent rendering if no book data

  const {
    title = 'Untitled',
    author = 'Unknown Author',
    category = 'Uncategorized',
    coverImage,
    avgRating
  } = book;

  // ✅ Ensure avgRating is always a valid number
  const safeRating = Number(avgRating) || 0;

  // ✅ Fallback image
  const imageUrl = coverImage || '/default-book-cover.jpg';

  return (
    <div className="bg-white shadow-md rounded-lg overflow-hidden hover:shadow-lg transition duration-300">
      <img
        src={imageUrl}
        alt={title}
        className="h-48 w-full object-cover"
        onError={(e) => {
          e.target.src = '/default-book-cover.jpg'; // ✅ Handle broken image links
        }}
      />
      <div className="p-4">
        <span className="block text-xs font-semibold text-indigo-600 uppercase">
          {category}
        </span>
        <h3 className="mt-1 text-lg font-bold text-gray-800">{title}</h3>
        <p className="text-sm text-gray-600">by {author}</p>
        <div className="mt-2">
          <Rating value={safeRating} />
        </div>
      </div>
    </div>
  );
};

export default BookCard;
