 import React, { useContext } from "react";
import { Link, useLocation } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { FiBook, FiUser, FiLogOut } from "react-icons/fi";
import { motion } from "framer-motion";

const Navbar = () => {
  const { auth, logout } = useContext(AuthContext);
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  const linkClasses = (path) =>
    `relative px-3 py-2 rounded-md text-sm font-medium transition 
     after:content-[''] after:absolute after:w-0 after:h-[2px] after:bg-white after:left-0 after:bottom-0 after:transition-all after:duration-300
     hover:after:w-full ${isActive(path) ? "bg-indigo-900 shadow-md after:w-full" : "hover:bg-indigo-600"}`;

  return (
    <motion.nav
      initial={{ y: -60, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.6, ease: "easeOut" }}
      className="bg-gradient-to-r from-indigo-800 via-indigo-700 to-indigo-800 text-white shadow-lg"
    >
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-2 text-white drop-shadow-md">
            <FiBook className="h-8 w-8" />
            <span className="font-bold text-xl">Racing Books</span>
          </Link>

          {/* Links */}
          <div className="hidden md:flex space-x-6">
            <Link to="/" className={linkClasses("/")}>Books</Link>

            {auth.isAuthenticated && (
              <Link to="/profile" className={linkClasses("/profile")}>
                My Profile
              </Link>
            )}

            {auth.isAuthenticated && auth.user?.role === "admin" && (
              <Link to="/admin" className={linkClasses("/admin")}>
                Admin Dashboard
              </Link>
            )}
          </div>

          {/* Right Side */}
          <div className="flex items-center">
            {auth.isAuthenticated ? (
              <div className="flex items-center space-x-4">
                <div className="flex items-center bg-indigo-500 px-3 py-1 rounded-full shadow-md">
                  {auth.user?.profilePhoto ? (
                    <img
                      src={auth.user.profilePhoto}
                      alt={auth.user.name}
                      className="w-8 h-8 rounded-full mr-2 border-2 border-white"
                    />
                  ) : (
                    <div className="bg-gray-200 rounded-full w-8 h-8 flex items-center justify-center text-gray-700 mr-2">
                      <FiUser />
                    </div>
                  )}
                  <span className="font-medium">{auth.user?.name}</span>
                </div>
                <button
                  onClick={logout}
                  className="flex items-center text-sm font-medium bg-red-500 hover:bg-red-600 px-3 py-2 rounded-md shadow-md transition"
                >
                  <FiLogOut className="mr-1" /> Logout
                </button>
              </div>
            ) : (
              <div className="flex items-center space-x-4">
                <Link
                  to="/login"
                  className="px-3 py-2 rounded-md text-sm font-medium bg-indigo-500 hover:bg-indigo-600 shadow-md transition"
                >
                  Login
                </Link>
                <Link
                  to="/register"
                  className="px-3 py-2 bg-white text-indigo-700 rounded-md text-sm font-medium hover:bg-gray-100 shadow-md transition"
                >
                  Register
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </motion.nav>
  );
};

export default Navbar;
