 import React, { useState } from "react";
import { FaUser, FaEnvelope, FaLock, FaPhone } from "react-icons/fa";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    mobile: "",
  });

  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const { name, email, password, mobile } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    try {
      const res = await fetch("http://localhost:5000/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, password, mobile }),
      });

      if (!res.ok) {
        const data = await res.json();
        throw new Error(data.error || "Registration failed");
      }

      setSuccess("✅ Account created successfully. You can now log in.");
      setFormData({ name: "", email: "", password: "", mobile: "" });
    } catch (err) {
      setError(err.message);
    }
  };

  const inputContainer = {
    position: "relative",
    marginBottom: "15px",
  };

  const iconStyle = {
    position: "absolute",
    top: "50%",
    left: "12px",
    transform: "translateY(-50%)",
    color: "#888",
  };

  const inputStyle = {
    width: "100%",
    padding: "12px 12px 12px 38px",
    border: "1px solid #ddd",
    borderRadius: "8px",
    fontSize: "14px",
    outline: "none",
    transition: "border 0.2s, box-shadow 0.2s",
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "linear-gradient(135deg, #6a11cb, #2575fc)",
        padding: "20px",
      }}
    >
      <div
        style={{
          background: "#fff",
          padding: "30px",
          width: "100%",
          maxWidth: "420px",
          borderRadius: "15px",
          boxShadow: "0 8px 20px rgba(0,0,0,0.15)",
          textAlign: "center",
        }}
      >
        <h2 style={{ marginBottom: "5px", fontSize: "24px", color: "#333" }}>
          Create Account
        </h2>
        <p style={{ fontSize: "14px", color: "#777", marginBottom: "20px" }}>
          Sign up to get started
        </p>

        {error && (
          <p style={{ color: "#d9534f", fontSize: "14px", marginBottom: "10px" }}>
            {error}
          </p>
        )}
        {success && (
          <p style={{ color: "#28a745", fontSize: "14px", marginBottom: "10px" }}>
            {success}
          </p>
        )}

        <form onSubmit={onSubmit}>
          <div style={inputContainer}>
            <FaUser style={iconStyle} />
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={name}
              onChange={onChange}
              required
              style={inputStyle}
            />
          </div>
          <div style={inputContainer}>
            <FaEnvelope style={iconStyle} />
            <input
              type="email"
              name="email"
              placeholder="Email Address"
              value={email}
              onChange={onChange}
              required
              style={inputStyle}
            />
          </div>
          <div style={inputContainer}>
            <FaPhone style={iconStyle} />
            <input
              type="text"
              name="mobile"
              placeholder="Mobile Number"
              value={mobile}
              onChange={onChange}
              required
              style={inputStyle}
            />
          </div>
          <div style={inputContainer}>
            <FaLock style={iconStyle} />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={password}
              onChange={onChange}
              required
              style={inputStyle}
            />
          </div>

          <button
            type="submit"
            style={{
              width: "100%",
              padding: "12px",
              background: "linear-gradient(90deg, #6a11cb, #2575fc)",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              fontSize: "16px",
              cursor: "pointer",
              fontWeight: "bold",
              transition: "opacity 0.2s",
            }}
            onMouseOver={(e) => (e.target.style.opacity = "0.9")}
            onMouseOut={(e) => (e.target.style.opacity = "1")}
          >
            Sign Up
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
