 import React from 'react';
import { Link } from 'react-router-dom';
import Rating from '../ui/Rating';
import { FiArrowRight } from 'react-icons/fi';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';

const BookCard = ({ book }) => {
  const { auth } = useAuth();

  const handleIssueBook = async () => {
    if (!auth.token) {
      alert('You must be logged in to issue a book.');
      return;
    }

    try {
      const res = await axios.put(
        `/api/books/${book._id}/issue`,
        {},
        { headers: { Authorization: `Bearer ${auth.token}` } }
      );
      alert('Book issued successfully!');
      window.location.reload();
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to issue book');
    }
  };

  return (
    <div className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-xl transition-shadow duration-300 border border-gray-100">
      <div className="relative">
        <img
          src={book.coverImage}
          alt={book.title}
          className="w-full h-64 object-cover"
        />
        <div className="absolute top-2 right-2 bg-indigo-600 text-white text-xs font-bold px-2 py-1 rounded">
          {book.category}
        </div>
      </div>

      <div className="p-5">
        <h3 className="text-xl font-bold text-gray-800 mb-2">{book.title}</h3>
        <p className="text-gray-600 mb-3">by {book.author}</p>

        <div className="flex items-center justify-between mb-4">
          <Rating value={book.avgRating} />
          <span className="text-sm text-gray-500">
            {book.issued ? 'Issued' : 'Available'}
          </span>
        </div>

        <div className="flex justify-between items-center">
          {book.issued ? (
            <button
              className="bg-gray-200 text-gray-700 px-4 py-2 rounded-lg font-medium cursor-not-allowed"
              disabled
            >
              Issued
            </button>
          ) : (
            <button
              onClick={handleIssueBook}
              className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-lg font-medium transition"
            >
              Issue Book
            </button>
          )}

          <Link
            to={`/books/${book._id}`}
            className="text-indigo-600 hover:text-indigo-800 font-medium flex items-center"
          >
            Details <FiArrowRight className="ml-1" />
          </Link>
        </div>
      </div>
    </div>
  );
};

export default BookCard;
