 import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiUser, FiEdit, FiSave } from 'react-icons/fi';

const ProfileHeader = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobile: '',
    profilePhoto: ''
  });

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.getaxios.get('/api/auth/me', 
 {
          headers: { Authorization: `Bearer ${token}` }
        });
        setUser(res.data);
        setFormData({
          name: res.data.name || '',
          email: res.data.email || '',
          mobile: res.data.mobile || '',
          profilePhoto: res.data.profilePhoto || ''
        });
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const res = await axios.put('/api/auth/profile', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(res.data);
      setIsEditing(false);
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center py-8">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="p-6 text-center text-gray-500">
        Unable to load profile.
      </div>
    );
  }

  return (
    <div className="bg-white rounded-xl shadow-md p-6">
      <div className="flex flex-col md:flex-row items-center md:items-start">
        <div className="relative mb-4 md:mb-0 md:mr-6">
          {user?.profilePhoto ? (
            <img
              src={user.profilePhoto}
              alt={user.name || 'User'}
              className="w-32 h-32 rounded-full object-cover border-4 border-indigo-100"
            />
          ) : (
            <div className="w-32 h-32 rounded-full bg-gray-200 border-4 border-indigo-100 flex items-center justify-center">
              <FiUser className="text-gray-500 text-4xl" />
            </div>
          )}
          {isEditing && (
            <input
              type="text"
              name="profilePhoto"
              value={formData.profilePhoto}
              onChange={handleChange}
              placeholder="Enter image URL"
              className="mt-2 w-full px-3 py-2 text-sm border border-gray-300 rounded"
            />
          )}
        </div>

        <div className="flex-1">
          <div className="flex justify-between items-start mb-4">
            {isEditing ? (
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="text-2xl font-bold text-gray-800 border-b border-gray-300 pb-1 w-full"
              />
            ) : (
              <h1 className="text-2xl font-bold text-gray-800">{user?.name}</h1>
            )}

            <button
              onClick={() => (isEditing ? handleSubmit() : setIsEditing(true))}
              className="flex items-center text-indigo-600 hover:text-indigo-800"
            >
              {isEditing ? <FiSave className="mr-1" /> : <FiEdit className="mr-1" />}
              {isEditing ? 'Save' : 'Edit Profile'}
            </button>
          </div>

          <div className="space-y-3">
            <div>
              <label className="text-sm font-medium text-gray-500">Email</label>
              {isEditing ? (
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded"
                />
              ) : (
                <p className="text-gray-800">{user?.email}</p>
              )}
            </div>

            <div>
              <label className="text-sm font-medium text-gray-500">Mobile</label>
              {isEditing ? (
                <input
                  type="tel"
                  name="mobile"
                  value={formData.mobile}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded"
                />
              ) : (
                <p className="text-gray-800">{user?.mobile}</p>
              )}
            </div>

            <div>
              <label className="text-sm font-medium text-gray-500">Member Since</label>
              <p className="text-gray-800">
                {user?.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'N/A'}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileHeader;
