 import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { FiArrowLeft, FiBook, FiCalendar, FiUser, FiStar } from 'react-icons/fi';
import Rating from '../ui/Rating';

const BookDetails = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [rating, setRating] = useState(0);
  const [isIssued, setIsIssued] = useState(false);
  const [userRating, setUserRating] = useState(0);

  // Get token once
  const token = localStorage.getItem('token');
  const headers = token ? { Authorization: `Bearer ${token}` } : {};

  // Decode token helper
  const getUserIdFromToken = () => {
    try {
      return token ? JSON.parse(atob(token.split('.')[1])).id : null;
    } catch {
      return null;
    }
  };

  useEffect(() => {
    const fetchBook = async () => {
      try {
        // Always fetch book details (public endpoint if no token)
        const res = await axios.get(`/api/books/${id}`, { headers });
        setBook(res.data);

        // If logged in, check if user issued the book & fetch rating
        if (token) {
          const currentUserId = getUserIdFromToken();

          if (res.data.issued && res.data.issuedTo?._id === currentUserId) {
            setIsIssued(true);
          }

          try {
            const ratingRes = await axios.get(`/api/books/${id}/rating`, { headers });
            if (ratingRes.data) {
              setUserRating(ratingRes.data.rating);
            }
          } catch (ratingErr) {
            if (ratingErr.response?.status !== 404) {
              console.warn("Rating fetch failed:", ratingErr.message);
            }
          }
        }

        setLoading(false);
      } catch (err) {
        console.error("Book fetch failed:", err.message);
        setLoading(false);
      }
    };

    fetchBook();
  }, [id]); // eslint-disable-line react-hooks/exhaustive-deps

  const handleIssue = async () => {
    if (!token) {
      alert("Please log in to issue books.");
      return;
    }
    try {
      await axios.put(`/api/books/${id}/issue`, {}, { headers });
      setIsIssued(true);
      const res = await axios.get(`/api/books/${id}`, { headers });
      setBook(res.data);
    } catch (err) {
      console.error("Issue book failed:", err.message);
    }
  };

  const handleReturn = async () => {
    if (!token) {
      alert("Please log in to return books.");
      return;
    }
    try {
      await axios.put(`/api/books/${id}/return`, {}, { headers });
      setIsIssued(false);
      const res = await axios.get(`/api/books/${id}`, { headers });
      setBook(res.data);
    } catch (err) {
      console.error("Return book failed:", err.message);
    }
  };

  const handleRate = async () => {
    if (!token) {
      alert("Please log in to rate books.");
      return;
    }
    try {
      await axios.post(`/api/books/${id}/rate`, { rating }, { headers });
      setUserRating(rating);
      const res = await axios.get(`/api/books/${id}`, { headers });
      setBook(res.data);
    } catch (err) {
      console.error("Rate book failed:", err.message);
    }
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8 flex justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (!book) {
    return (
      <div className="container mx-auto px-4 py-8">
        <p className="text-center text-xl text-gray-600">Book not found</p>
        <Link to="/" className="mt-4 block text-center text-indigo-600 hover:underline">
          Back to books
        </Link>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <Link to="/" className="flex items-center text-indigo-600 hover:text-indigo-800 mb-6">
        <FiArrowLeft className="mr-1" /> Back to Books
      </Link>

      <div className="bg-white rounded-xl shadow-md overflow-hidden">
        <div className="md:flex">
          <div className="md:flex-shrink-0 p-8 flex items-center justify-center bg-gray-50">
            <img
              className="h-64 w-auto object-cover rounded-lg shadow-md"
              src={book.coverImage}
              alt={book.title}
            />
          </div>
          <div className="p-8">
            <div className="flex items-center justify-between">
              <span className="bg-indigo-100 text-indigo-800 text-xs font-semibold px-3 py-1 rounded-full">
                {book.category}
              </span>
              <Rating value={book.avgRating} />
            </div>

            <h1 className="text-3xl font-bold text-gray-800 mt-2">{book.title}</h1>
            <p className="text-xl text-gray-600 mt-1">by {book.author}</p>

            <div className="mt-6 space-y-4">
              <div className="flex items-center">
                <FiCalendar className="text-gray-500 mr-2" />
                <span className="text-gray-700">Published: {book.publisherYear}</span>
              </div>

              <div className="flex items-center">
                <FiBook className="text-gray-500 mr-2" />
                <span className={`font-medium ${book.issued ? 'text-red-600' : 'text-green-600'}`}>
                  {book.issued ? 'Issued' : 'Available'}
                </span>
              </div>

              {book.issued && book.issuedTo && (
                <div className="flex items-center">
                  <FiUser className="text-gray-500 mr-2" />
                  <span className="text-gray-700">Issued to: {book.issuedTo.name}</span>
                </div>
              )}
            </div>

            <div className="mt-6">
              <h3 className="text-lg font-medium text-gray-800">Description</h3>
              <p className="mt-2 text-gray-600">{book.description}</p>
            </div>

            <div className="mt-8 flex flex-wrap gap-4">
              {isIssued ? (
                <button
                  onClick={handleReturn}
                  className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-6 rounded-lg transition"
                >
                  Return Book
                </button>
              ) : (
                <button
                  onClick={handleIssue}
                  disabled={book.issued}
                  className={`${book.issued ? 'bg-gray-400 cursor-not-allowed' : 'bg-indigo-600 hover:bg-indigo-700'} text-white font-bold py-3 px-6 rounded-lg transition`}
                >
                  {book.issued ? 'Not Available' : 'Issue Book'}
                </button>
              )}

              {token ? (
                userRating > 0 ? (
                  <div className="flex items-center bg-green-100 px-4 py-2 rounded-lg">
                    <FiStar className="text-yellow-500 mr-1" />
                    <span className="text-green-800">Your rating: {userRating}</span>
                  </div>
                ) : (
                  <div className="flex items-center">
                    <span className="mr-2 text-gray-700">Rate this book:</span>
                    <select
                      value={rating}
                      onChange={(e) => setRating(parseInt(e.target.value))}
                      className="border border-gray-300 rounded px-3 py-2"
                    >
                      <option value="0">Select</option>
                      <option value="1">1</option>
                      <option value="2">2</option>
                      <option value="3">3</option>
                      <option value="4">4</option>
                      <option value="5">5</option>
                    </select>
                    <button
                      onClick={handleRate}
                      disabled={rating === 0}
                      className={`${rating === 0 ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-600 hover:bg-green-700'} text-white font-bold py-3 px-6 rounded-lg transition ml-2`}
                    >
                      Rate
                    </button>
                  </div>
                )
              ) : (
                <p className="text-sm text-gray-500 mt-2">
                  Log in to issue, return, or rate books.
                </p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
