 import React, { useContext, useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { FiBook, FiUser, FiLogOut, FiMenu, FiX } from 'react-icons/fi';

const Navbar = () => {
  const { auth, logout } = useContext(AuthContext);
  const location = useLocation();
  const [menuOpen, setMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  const isActive = (path) => location.pathname === path;

  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 20);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const menuLinks = [
    { label: 'Books', path: '/' },
    ...(auth.isAuthenticated ? [{ label: 'My Profile', path: '/profile' }] : []),
    ...(auth.isAuthenticated && auth.user?.role === 'admin'
      ? [{ label: 'Admin Dashboard', path: '/admin' }]
      : []),
  ];

  const authLinks = !auth.isAuthenticated
    ? [
        { label: 'Login', path: '/login' },
        { label: 'Register', path: '/register' },
      ]
    : [];

  return (
    <nav
      className={`fixed w-full z-50 transition-all duration-500 backdrop-blur-md ${
        scrolled
          ? 'bg-indigo-800/60 shadow-lg'
          : 'bg-white/10 shadow-none'
      }`}
    >
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          
          {/* Logo */}
          <Link to="/" className="flex items-center hover:opacity-90 transition">
            <FiBook className="h-8 w-8 mr-2" />
            <span className="font-bold text-xl">Racing Books</span>
          </Link>

          {/* Desktop Menu */}
          <div className="hidden md:flex items-center space-x-6">
            {menuLinks.map((item) => (
              <NavLink key={item.path} to={item.path} active={isActive(item.path)}>
                {item.label}
              </NavLink>
            ))}
          </div>

          {/* Right Side Auth */}
          <div className="hidden md:flex items-center">
            {auth.isAuthenticated ? (
              <div className="flex items-center space-x-4">
                {/* Profile */}
                <div className="flex items-center bg-indigo-600/70 px-3 py-1 rounded-full hover:bg-indigo-500/70 transition">
                  {auth.user?.profilePhoto ? (
                    <img 
                      src={auth.user.profilePhoto} 
                      alt={auth.user.name} 
                      className="w-8 h-8 rounded-full mr-2 border border-white"
                    />
                  ) : (
                    <div className="bg-gray-200 border border-gray-300 rounded-full w-8 h-8 flex items-center justify-center text-gray-500 mr-2">
                      <FiUser />
                    </div>
                  )}
                  <span>{auth.user?.name}</span>
                </div>

                {/* Logout */}
                <button
                  onClick={logout}
                  className="flex items-center text-sm font-medium hover:text-gray-200 transition"
                >
                  <FiLogOut className="mr-1" /> Logout
                </button>
              </div>
            ) : (
              <div className="flex items-center space-x-3">
                {authLinks.map((link) => (
                  <Link 
                    key={link.path}
                    to={link.path}
                    className={`px-4 py-2 rounded-md text-sm font-medium ${
                      link.label === 'Register'
                        ? 'bg-white/80 text-indigo-700 hover:bg-white'
                        : 'hover:bg-indigo-600/50'
                    } transition`}
                  >
                    {link.label}
                  </Link>
                ))}
              </div>
            )}
          </div>

          {/* Mobile Menu Toggle */}
          <div className="md:hidden">
            <button onClick={() => setMenuOpen(!menuOpen)} className="text-white focus:outline-none">
              {menuOpen ? <FiX size={24} /> : <FiMenu size={24} />}
            </button>
          </div>
        </div>

        {/* Mobile Dropdown */}
        {menuOpen && (
          <div className="md:hidden flex flex-col space-y-2 mt-2 pb-4 border-t border-white/20">
            {[...menuLinks, ...authLinks].map((item, index) => (
              <div
                key={item.path}
                style={{ animationDelay: `${index * 0.1}s` }}
                className="animate-slideFadeDown"
              >
                <NavLink to={item.path} active={isActive(item.path)} mobile>
                  {item.label}
                </NavLink>
              </div>
            ))}

            {auth.isAuthenticated && (
              <div
                style={{ animationDelay: `${menuLinks.length * 0.1}s` }}
                className="animate-slideFadeDown"
              >
                <button
                  onClick={logout}
                  className="flex items-center space-x-2 text-left px-3 py-2 text-sm hover:bg-indigo-600/50 transition w-full"
                >
                  <FiLogOut /> <span>Logout</span>
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Animation & Hover Styles */}
      <style>
        {`
          @keyframes slideFadeDown {
            0% { opacity: 0; transform: translateY(-10px); }
            100% { opacity: 1; transform: translateY(0); }
          }
          .animate-slideFadeDown {
            animation: slideFadeDown 0.3s ease forwards;
          }
          .nav-underline {
            position: relative;
            display: inline-block;
          }
          .nav-underline::after {
            content: '';
            position: absolute;
            left: 0;
            bottom: -2px;
            width: 0%;
            height: 2px;
            background-color: white;
            transition: width 0.3s ease;
          }
          .nav-underline:hover::after {
            width: 100%;
          }
        `}
      </style>
    </nav>
  );
};

const NavLink = ({ to, active, children, mobile }) => (
  <Link
    to={to}
    className={`px-3 py-2 rounded-md text-sm font-medium transition nav-underline ${
      active ? 'border-b-2 border-white text-white' : 'text-white hover:text-gray-200'
    } ${mobile ? 'block' : ''}`}
  >
    {children}
  </Link>
);

export default Navbar;
